// 是否进入测试模式，开发者专用选项，平民玩家开了也没用
let testMode = 0;
try {
var defaultPic = 'http://1t.click/aXjm';

var data = JSON.parse(getResCode()).data.map(function(o) {
  return {
    title: o.title,
    desc: o.rate
      ? o.rate + '分'
      : (o.casts.length > 0 ? o.casts : o.directors).slice(0, 2).join(' / '),
    pic_url: o.cover.includes('default') && defaultPic ? defaultPic : o.cover,
    url: 'https://m.douban.com/movie/subject/' + o.id + '/' + '#' + o.title + '#noHistory#'
  };
});

setHomeResult({ data: data });

} catch (e) {
    let result = {};
    let errorTitle = '““””<font color="grey">' + '暂无内容，请稍候再试' + '</font>';
    let errorDesc = '';
    try {
         result = JSON.parse(getResCode());
    } catch (e) { }
    if (result.msg != null && result.msg !== '') {
         errorTitle = '““' + result.msg + '””';
         errorDesc = '提示：也许只是服务器打了个盹等等就会好起来了？\n提示：也许你可以试试搜索？有惊喜哦～';
    }

    setHomeResult({
          data: [{
              title: errorTitle,
              desc: errorDesc,
              url: testMode === 1 ? 'https://m.douban.com/subject/26357307/' : '',
              col_type: 'text_center_1'
          }]
    });
}

