    headers = ';get;UTF-8;{User-Agent@Android&&Cookie@' + cookie + '}';
}

var html = getResCode();
var list = parseDomForArray(html, '.topic-list&&.topic-item');
for (let j = 0; j < list.length; j++) {
    let author = parseDomForHtml(list[j], '.topic-author&&Text');
    let metaItems = parseDomForArray(list[j], '.topic-meta&&.meta-item');
    // setError(JSON.stringify(metaItems));
    let metaNode = parseDomForHtml(metaItems[1], 'Text');
    let meta = parseDomForHtml(metaItems[0], 'Text')
        + '  '
        + '<font color="' + nodeColor + '">' + metaNode + '</font>';
    let metaTags = parseDomForArray('<div>' + metaItems[2] + '</div>', 'div&&.tag');
    // setError(JSON.stringify(metaTags));
    let metaTag = '';
    for (let i = 0; i < metaTags.length; i++) {
        metaTag = (metaTag === '' ? metaTag : (metaTag + '  '))
            + parseDomForHtml(metaTags[i], 'Text')
    }
    metaTag = metaTag.replace('置顶', '““置顶””');
    meta = meta + '  ' + metaTag;
    let desc = author + ' ““””' + meta;
