        fileToken = fetch ('hiker://files/rules/js/hikerbbs_token.js', {});
        if (fileToken == null) fileToken = '';
    } catch (e) {
        fileToken = '';
    }
}
var cookie = fileToken === '' ? '' : 'userToken=' + fileToken;
