if (cookie !== '') {
    headers = ';get;UTF-8;{User-Agent@Android&&Cookie@' + cookie + '}';
}

/**
 * 获取搜索源码
 * etc.
 * hiker://empty/#**#fypage
 * https://www.dogedoge.com/results?q=site:haikuoshijie.cn+规则&p=fypage
 */
let urlSplits = MY_URL.split('#');
let searchUrl = 'https://www.dogedoge.com/results?q=site:haikuoshijie.cn+'
    + urlSplits[1]
    + '&p='
    + urlSplits[2];
let searchCookies = getVar('doge_token');
if (searchCookies.indexOf('token') < 0 || searchCookies.indexOf('vID') < 0) {
    let searchInitHtml = fetch(searchUrl, {
        headers: {
            'User-Agent': MOBILE_UA
        },
        withHeaders: true,
        withStatusCode: true,
        redirect: false,
    });
    let searchHeaders = JSON.parse(searchInitHtml).headers;
    for (let i = 0; i < searchHeaders["set-cookie"].length; i++) {
        searchCookies += (searchHeaders["set-cookie"][i] + ';');
    }
    putVar2('doge_token', searchCookies);
}
let html = fetch(searchUrl, {
    headers: {
        'User-Agent': MOBILE_UA,
        'cookie': searchCookies
    },
})

// setError(html);

var list = parseDomForArray(html, '.results&&.result');
// setError(JSON.stringify(list));
for (var j in list) {
