js:
var res = {};
var d = [];
var urlSplits = MY_URL.split("#");
var mUrl = urlSplits[0];
var page = urlSplits[1];
var colType = 'rich_text';

// 是否显示帖子分类，显示/1 不显示/0
var showMeta = 1;
// 帖子分类字体大小 big/大 span/中 small/小
var metaSize = 'small';
// 帖子分类颜色
var nodeColor = "#2979FF";
// 是否显示正文，显示/1 不显示/0
var showContent = 1;
// 是否显示评论，显示/1 不显示/0
var showComment = 1;
// 是否隐藏图片，隐藏/1 不隐藏/0
var hideImg = 0;
// 使用 emoji 图标替换文本，使用/1 不使用/0
var useEmoji = 1;
// 评论用户颜色
var userColor = "#2979FF";
// 评论回复引用用户颜色
var quoteUserColor = "black";

/**
 * 用户 Token (需要先登录才能获取)
 *
 * 抓包 Cookie 中的 userToken= 后面那串东西
 * 或
 * 不填写固定值 则需配合专用插件自动获取
 * 但每次运行视界都需打开一次论坛
 */
var token = '';

/**
 * 是否使用插件自动获取 Token 的方式
 *
 * 是/1 否/0
 */
var autoToken = 0;

/**
 * 通过插件自动获取 Token 到文件中然后自动读取
 *
 * 注意：以上方的 token 为主
 */
var fileToken = getVar('hiker_token');
if (fileToken === '') {
try {
    fileToken = fetch ('hiker://files/rules/js/hikerbbs_token.js', {});
    if (fileToken == null) fileToken = '';
} catch (e) {
    fileToken = '';
}
}
token = token === '' ? fileToken : token;
var showToken = token === '' ? getVar("hikerbbs") : token;
if (autoToken) token = showToken;
// if (token === '' && showToken !== '') setError(showToken);

// 是否显示填写 Token 提示，显示/1 不显示/0
var showInputTokenTips = 1;
// 是否开启收藏/点赞结果提示，是/1 否/0
var resultTips = 1;
// 第几套图标，-1为随机，从下面三个数组挑选
var iconIndex = -1;
// 收藏图标
var starIcons = [ 'https://www.easyicon.net/api/resizeApi.php?id=1286217&size=128',
];
// 点赞图标
var likeIcons = [ 'https://www.easyicon.net/api/resizeApi.php?id=1288217&size=128',
    /*'https://www.easyicon.net/api/resizeApi.php?id=1211507&size=128',
    'https://www.easyicon.net/api/resizeApi.php?id=1239511&size=128'*/
];
// 回复图标
var commentIcons = [ 'https://www.easyicon.net/api/resizeApi.php?id=1239495&size=128',
];
// 原文章图标
/*var articleIcons = [ 'https://www.easyicon.net/api/resizeApi.php?id=1238522&size=128',
];*/

let imgError = '<p><font color="grey">图片暂时无法显示，若要查看请<a href="' + MY_URL + '">访问原文</a></font></p>';
String.prototype.replaceWithConfirm = function (confirmValue, searchValue, replaceValue) {
    let returnValue = this;
    if (confirmValue) {
        returnValue = this.replace(searchValue, replaceValue)
    }
    return returnValue;
};
String.prototype.replaceWithCallback = function (searchValue, replaceValue, callback) {
    let returnValue = this;
    if (callback != null) {
        returnValue = callback(this, searchValue, replaceValue);
    }
    return returnValue;
};
function imgTag(context, imgUrl) {
    let replaceString = imgUrl === '' ? imgError : '<p><showimg src="' + (imgUrl + '@Referer=https://haikuoshijie.cn') + '"></p>';
    if (hideImg) {
        replaceString = imgUrl === '' ? imgError : '<p><a href="' + imgUrl + '">[点击查看大图]</a></p>';
    }
    return context.replace(/<img data-v>/g, replaceString);
}
function imgTagReplacement2(context, searchValue, replaceValue) {
    let matchArray = context.match(searchValue);
    let returnValue = context;
    if (matchArray != null) {
        for (let i = 0; i < matchArray.length; i++) {
            let item = matchArray[i];
            /<img (src|data-src)="(.*?)"([\s\S]*?)>/.test(item);
            let imgUrl = RegExp.$2;
            if (imgUrl !== '') {
                returnValue = returnValue.replace(item, replaceValue);
                returnValue = imgTag(returnValue, imgUrl);
            }
        }
    }
    return returnValue;
}
function abbreviateReplyText(context, textArray) {
    let content = context;
    for (let i = 0; i < textArray.length; i++) {
        let text = parseDomForHtml(textArray[i], "p&&Text");
        if (text.length > 30) {
            content = content.replace(text, text.substring(0, 30) + "......");
        }
    }
    return content;
}

if (page > 1) {

    let cursor = getVar("cursor");
    if (page === '2') cursor = getVar("first_page_cursor");
    cursor = cursor === '' ? '0' : cursor;

    let urlTemplate = "http://haikuoshijie.cn/api/comment/list?entityType=topic&entityId={entityId}&cursor={cursor}";
    if (/topic\/(\d+)/.test(MY_URL) === true) {
        let entityId = RegExp.$1;
        if (entityId != null && entityId !== "") {
            let noMore = getVar("noMore" + entityId);
            noMore = noMore === '' ? '0' : noMore;
            urlTemplate = urlTemplate.replace("{entityId}", entityId);
            url = urlTemplate.replace("{cursor}", cursor);
            // url = "http://haikuoshijie.cn/api/comment/list?entityType=topic&entityId=398&cursor=9183";
            let json = {};
            try {
                json = JSON.parse(fetch(url, {}));
                let data = json.data;
                let results = data.results;
                if (results != null) {
                    let nowTime = new Date().getTime();
                    putVar({key: 'cursor', value: data.cursor});

                    function timeDiff(nowTime, createTime) {
                        Date.prototype.format = function (fmt) {
                            let o = {
                                "M+": this.getMonth() + 1,                 //月份
                                "d+": this.getDate(),                    //日
                                "h+": this.getHours(),                   //小时
                                "m+": this.getMinutes(),                 //分
                                "s+": this.getSeconds(),                 //秒
                                "q+": Math.floor((this.getMonth() + 3) / 3), //季度
                                "S": this.getMilliseconds()             //毫秒
                            };
                            if (/(y+)/.test(fmt)) {
                                fmt = fmt.replace(RegExp.$1, (this.getFullYear() + "").substr(4 - RegExp.$1.length));
                            }
                            for (let k in o) {
                                if (new RegExp("(" + k + ")").test(fmt)) {
                                    fmt = fmt.replace(RegExp.$1, (RegExp.$1.length === 1) ? (o[k]) : (("00" + o[k]).substr(("" + o[k]).length)));
                                }
                            }
                            return fmt;
                        };
                        let time = new Date(createTime).format("yyyy-MM-dd hh:mm:ss");
                        let timeDiff = nowTime - createTime;
                        let days = Math.floor(timeDiff / (24 * 3600 * 1000)); // 计算出天数
                        let dayLeave = timeDiff % (24 * 3600 * 1000); // 计算天数后剩余的时间
                        let hours = Math.floor(dayLeave / (3600 * 1000)); // 计算天数后剩余的小时数
                        let hourLeave = timeDiff % (3600 * 1000); // 计算剩余小时后剩余的毫秒数
                        let minutes = Math.floor(hourLeave / (60 * 1000)); // 计算剩余的分钟数
                        if (0 < days && days < 31) {
                            time = days + "天前"
                        } else if (days < 0) {
                            if (hours > 0) {
                                time = hours + "小时前";
                            } else {
                                time = minutes + "分钟前";
                            }
                        }
                        return time;
                    }

                    for (let i = 0; i < results.length; i++) {
                        let result = results[i];
                        let quoteContent = '';
                        if (result.quote != null) {
                            let quote = result.quote;
                            let content = parseDomForHtml(quote.content, 'body&&Html');
                            let nickname = quote.user.nickname;

                            // https://blog.csdn.net/GrootBaby/article/details/99980395
                            let createTime = quote.user.createTime;
                            let time = timeDiff(nowTime, createTime);

                            try {
                                content = content
                                    .replace(/<a[\s\S].*?<\/a>/g, '');
                                let commentReplyTextArray = content.match(/<p[\s\S].*?<\/p>/g);
                                content = abbreviateReplyText(content, commentReplyTextArray);
                            } catch (e) {
                            }
                            quoteContent = '<blockquote class="comment-quote"><div class="comment-quote-user"><a class="quote-nickname"><font color="' + quoteUserColor + '">'
                                + nickname
                                + '</font></a><span class="quote-time"><font color="grey">'
                                + '\t\t'
                                + time
                                + '</font></span></div><div itemprop="text">'
                                + content
                                + '</div></blockquote>';
                        }
                        let content = '<p>' + result.content + '</p>';
                        let meta = '<br><small><font color="' + userColor + '">'
                            + result.user.nickname
                            + '</font>\t\t<font color="grey">'
                            + timeDiff(nowTime, result.createTime)
                            + '\t\t回复'
                            + '</font></small>';
                        let commentContent = quoteContent + content + meta;
                        let title = commentContent
                            .replace(/<img class="avatar([\s\S]*?)>/g, '')
                            .replace(/(<(p)([\s\S]*?)>)|<p>|<\/p>/g, '')
                            .replaceWithCallback(/<img (src|data-src)="(.*?)"([\s\S]*?)>/g, "<img data-v>", imgTagReplacement2)
                            .replace(/<img([\s\S]*?)>/g, imgError)
                            .replace(/<showimg/g, '<img');
                        d.push({
                            title: title,
                            desc: '',
                            url: MY_URL,
                            col_type: colType
                        });
                        d.push({
                            col_type: 'line'
                        });
                    }
                } else {
                    if (noMore) {
                        d.push({
                            title: "<p></p>" + '<center><font color="grey">已无更多评论</font></center>',
                            col_type: colType
                        });
                        putVar({key: 'noMore' + entityId, value: 1});
                    }
                }
            } catch (e) {
            }
        }
    }
} else {
    var html = getResCode();
try {
    var js = parseDom(html, '.main-content&&Html');
    var hasRule = 0;

    // setError(html);

    try {

        let title = '<p></p><big style="padding-left: 15px"><strong>' + parseDomForHtml(html, '.topic-header-center&&h1&&Html') + "</strong></big>";
        let meta = '';
        // parseDomForHtml(html,'.topic-header-center&&.topic-meta&&Html')
        let count = '<span style="color: grey">' + parseDomForHtml(html, '.topic-header-center&&.topic-header-right&&Text') + "</span>";

        if (showMeta) {
            let metaItems = parseDomForArray(html, '.topic-meta&&.meta-item');
            // setError(JSON.stringify(metaItems));
            let metaNode = parseDomForHtml(metaItems[2], 'Text');
            meta = '<font color="grey">' + parseDomForHtml(metaItems[0], 'Text')
                + '  '
                + parseDomForHtml(metaItems[1], 'Text')
                + '  '
                + '</font><font color="' + nodeColor + '">' + metaNode + '</font><font color="grey">';
            let metaTags = parseDomForArray('<div>' + metaItems[3] + '</div>', 'div&&.tag');
            // setError(JSON.stringify(metaTags));
            let metaTag = '';
            for (let i = 0; i < metaTags.length; i++) {
                metaTag = (metaTag === '' ? metaTag : (metaTag + '  '))
                    + parseDomForHtml(metaTags[i], 'Text')
            }
            // metaTag = metaTag.replace('置顶', '““置顶””');
            metaSize = metaSize === '' ? 'small' : metaSize;
            meta = '<' + metaSize + '>' + meta + '  ' + metaTag + '</font></' + metaSize + '>' + '<br>';
            // setError(meta);
        }

        // 👍🏻❤️🧡♡♥💬👁️➿𓁺
        if (useEmoji)
            count = count.replace("获赞：", "❤️ ")
                .replace("评论：", "💬 ")
                .replace("查看：", "👁️ ");

        let header = "" + title + '\t<small><font color="grey"><a href="' + MY_URL + '">[查看原文]</a></font></small>' + "<br>" + meta + count + '';

        d.push({
            title: header,
            col_type: colType
        });

        for (let i = 0; i < 2; i++)
            d.push({
                col_type: 'line'
            });

        if (showContent) {
            let title = parseDomForHtml(html, '.content&&Html')
                .replaceWithCallback(/<img (src|data-src)="(.*?)"([\s\S]*?)>/g, "<img data-v>", imgTagReplacement2)
                .replace(/<img([\s\S]*?)>/g, imgError)
                .replace(/<showimg/g, '<img')
                .replace(/(<(div)([\s\S]*?)>)|<div>|<\/div>/g, '');
            d.push({
                title: "<p></p>" + title
                ,
                desc: '',
                url: '',
                col_type: colType
            });

        }

        var regExp = /(.*?)￥(.*?)￥(.*)/;
        if (js.indexOf('>海阔') !== -1) {

            /*if (showContent) {
            for(var i = 0; i < 2; i++)
            d.push({
                   col_type: 'line'
              });
            }*/

            let col = "text_1";
            let list = js.match(/>海阔[\s\S]*?<\//g);
            if (list != null && list.length !== 0) hasRule = 1;
            if (list != null && list.length > 1) {
                d.push({
                    title: "<p></p>" + "<big><b>本文所有规则一键导入</b></big>",
                    col_type: colType
                });
            }
            for (let j = 0; j < list.length; j++) {
                let title = list[j].split('>')[1].split('</')[0];
                let mTitle = title;

                if (regExp.test(title) === true) {
