                }
                title = (mTitle != null && mTitle !== '') ? mTitle : title;
                if (title.length > 14) col = "text_2";

                d.push({
                    title: title,
                    desc: '🔔‘‘’’<b> 点击导入 </b>🔔',
                    url: 'rule://' + base64Encode(decodeURIComponent(list[j].split('>')[1].split('</')[0].replace(/amp;/g, '')
                        .replace(/&lt;/g, '<')
                        .replace(/&gt;/g, '>'))),
                    col_type: col
