            commentIcon = commentIcons[iconIndex > commentIconMax ? commentIconMax : iconIndex];
            // articleIcon = articleIcons[iconIndex > articleIconMax ? articleIconMax : iconIndex];
            let isStarred = 0;
            let starStatus = '';
            try {
                starStatus = parseDomForHtml(html, ".favorite.active&&Html");
            } catch (e) {
                starStatus = ''
            }
            if (starStatus !== '') isStarred = 1;
            // setError(starStatus);
            d.push({
                col_type: 'line',
            });
            d.push({ //  text_center_1 icon_small_3
                title: isStarred === 0 ? '收藏' : '已收藏',
