            if(page === 1) {
                let contentTitle = getBigText(parseDomForHtml(contentsHtml, '.block-title&&Text'));
                d.push({
                    title: contentTitle
                });
            }
            let contentsHtmlArray = parseDomForArray(contentsHtml, '.normal-recipe-list&&li');
            for (let i = 0; i < contentsHtmlArray.length; i++) {
                let contentItemHtml = contentsHtmlArray[i];
                let cover = parseDomForHtml(contentItemHtml, '.cover&&img&&data-src');
                let title = parseDomForHtml(contentItemHtml, '.info&&.name&&Text');
                let desc = parseDomForHtml(contentItemHtml, '.stats&&Text');
                let url = parseDom(contentItemHtml, 'a&&href');
                d.push({
                    title: title,
                    pic_url: cover,
                    desc: desc,
                    url: url + rule,
                    col_type: 'movie_1'
                })
            }
        } catch (e) { }
    } else {
        /**
         * header
         */
        try {
            let headerTitle = getBigText(getStrongText(parseDomForHtml(mainHtml, 'h1&&Text')))
                + ' ' + getSmallText(getAddressTag(MY_URL, '[查看原文]'));
            let tags = '';
            try {
                let headerTagsHtml = parseDomForHtml(html, '.recipe-tags&&Html');
                let tagsHtmlArray = parseDomForArray(headerTagsHtml, '.recipe-cats&&a');
                // setError(JSON.stringify(tagsHtmlArray));
                for (let i = 0; i < tagsHtmlArray.length; i++) {
                    let tagHtml = tagsHtmlArray[i];
                    let tag = getCodeText(parseDomForHtml(tagHtml, 'Text'));
                    tags += tag;
                }
                // setError(tags);
            } catch (e) { }
            tags = tags === '' ? '<br>' + getCodeText('暂无分类') : '<br>' + tags;
            d.push({
                title: headerTitle + tags
            });
            d.push({
                col_type: 'line'
            });
        } catch (e) { }

        /**
         * 描述
         */
        try {
            let author = getAuthorText('作者：' + parseDomForHtml(mainHtml, '.author&&Text'));
            let desc = '';
            try {
                desc = '<br>' + parseDomForHtml(mainHtml, '.desc&&Html');
            } catch (e) { }

            /**
             * 展示
             */
            let showVideo = '';
            try {
                let showHtml = parseDomForHtml(html, '.recipe-show&&Html');
                let iframeVideoUrl = parseDomForHtml(showHtml, 'iframe&&src');
                // setError(iframeVideoUrl);
                let iframeHtml = fetch(iframeVideoUrl, {});
                // setError(iframeHtml);
                let videoUrl = parseDomForHtml(iframeHtml, 'video&&src');
                showVideo = getAddressTag(videoUrl, '本帖含视频，点我查看～');
            } catch (e) { }

            let ings = '';
            try {
                /**
                 * 用料
                 */
                let ingsHtml = parseDomForHtml(mainHtml, '.ings&&Html');
                let ingsHtmlArray = parseDomForArray(ingsHtml, 'table&&tr');
                // setError(JSON.stringify(ingsHtmlArray));
                for (let i = 0; i < ingsHtmlArray.length; i++) {
                    let ingHtml = ingsHtmlArray[i];
                    /<td class="name">([\s\S]*?)<\/td>/.test(ingHtml);
                    let ingName = RegExp.$1;
                    if (ingName !== '') {
                        ingName = getColorText(parseDomForHtml(ingName, 'Text'), ingNameColor);
                        /<td class="unit">([\s\S]*?)<\/td>/.test(ingHtml);
                        let ingUnit = RegExp.$1;
                        ingUnit = getColorText(((ingUnit === ingName) ? '未知' : ingUnit), ingUnitColor);
                        let ing = ingName + ' ' + ingUnit;
                        ing = i === (ingsHtmlArray.length - 1) ? ing : ing + (ingsMode === 1 ? '<br>' : '、');
                        ings += ing
                    }
                }
                ings = (getBigText(getTitleText('用料')) + '<br>') + ings;
            } catch (e) { }
            desc = showDesc === 1 ? desc : '';
            if (model_line_height > 0) {
                d.push({
                    title: getBlockquoteText(author + desc) + (showVideo === '' ? getGreyText(contentTips) : showVideo + '<br>')
                });
                for(let i = 0; i < model_line_height; i++) {
                    d.push({
                        col_type: 'line_blank'
                    });
                }
                d.push({
                    title: ings
                });
                for(let i = 0; i < model_line_height; i++) {
                    d.push({
                        col_type: 'line_blank'
                    });
                }
            } else {
                d.push({
                    title: getBlockquoteText(author + desc) + (showVideo === '' ? '' : showVideo + '<br><br>') + ings
                });
            }
        } catch (e) { }

        /**
         * 正文
         */
        try {
            let stepsTitle = getBigText(getTitleText('' +  parseDomForHtml(mainHtml, '#steps&&Text') + ''));
            let stepsHtmlArray = parseDomForArray(mainHtml, '.steps&&.container');
            if(multiStepModel && model_line_height > 0) {
                d.push({
                    title: stepsTitle
                });
            }
            let steps = '';
            for (let i = 0; i < stepsHtmlArray.length; i++) {
                let step = parseDomForHtml(stepsHtmlArray[i], '.container&&Html');
                step = getStrongText('步骤' +(i + 1) + '<br>')
                    + step
                        .replace(/<\/p> <img src=/g, '<br><img src=')
                        .replaceWithCallback(/(<img (src|data-src)="(.*?)"([\s\S]*?)>)/g, "<img data-v>", imgTagReplacement2)
                        .replace(/(<p class="text" style=".*?">)|(<\/p>)/, '');
                if(multiStepModel && model_line_height > 0) {
                    d.push({
                        title: step
                    })
                } else {
                    steps += '<br><br>' + step;
                }
            }
            if (!(multiStepModel && model_line_height > 0)) {
                d.push({
                    title: stepsTitle + steps
                });
            }
            for(let i = 0; i < model_line_height; i++) {
                d.push({
                    col_type: 'line_blank'
                });
            }
            /**
             * Tips
             */
            let tips = '';
            try {
                let tipsHtml = parseDomForHtml(mainHtml, '.tip-container&&Html');
                let tipsTitle = getBigText(getTitleText(parseDomForHtml(tipsHtml, 'h2&&Text')));
                tipsContent = '<br>' + parseDomForHtml(tipsHtml, '.tip&&Html');
                tips = getColorText(tipsTitle + tipsContent, tipsColor);
                d.push({
                    title: tips
                });
                for(let i = 0; i < model_line_height; i++) {
                    d.push({
                        col_type: 'line_blank'
                    });
                }
            } catch(e) { }
        } catch (e) { }

        /**
         * footer
         */
        try {
            let footerHtml = parseDomForHtml(html, '.recipe-stats&&Html');
            let time = parseDomForHtml(footerHtml, '.time&&Text');
            let collectionCount = parseDomForHtml(footerHtml, '.pv&&Text');
            let copyright = parseDomForHtml(footerHtml, '.copyright&&Text');
            let footer = getSmallText(getGreyText(time + '<br>' + collectionCount + '<br>' + copyright))
            d.push({
                title: footer
            });
        } catch (e) { }

        /**
         * 评论
         */
        try {
            let commentHtml = parseDomForHtml(html, '.page-bottom-outer&&.page-container&&Html');
            let commentTitle = getBigText(getTitleText(parseDomForHtml(commentHtml, 'h2&&Text')));
            let commentsHtmlArray = parseDomForArray(commentHtml, '.question-list&&.recipe-quesions-content');
            if (commentsHtmlArray.length > 0) {
                d.push({
                    col_type: 'line_blank'
                });
                d.push({
                    title: commentTitle
                });
            }
            let comment = '';
            for (let i = 0; i < commentsHtmlArray.length; i++) {
                let commentsBlockHtmlArray = parseDomForArray(commentsHtmlArray[i], 'li&&.avatar-right-2');
                for (let j = 0; j < commentsBlockHtmlArray.length; j++) {
                    let replyComment = '';
                    let authorComment = '';
                    if (commentsBlockHtmlArray[j].indexOf('赞') > -1) {
                        let replier = getAuthorText(parseDomForHtml(commentsBlockHtmlArray[j], '.info&&a&&Text'));
                        let reply_time = '';
                        if (/(<span class="normal-font gray-font align-baseline">[\s\S]*?<\/span>)/.test(commentsBlockHtmlArray[j]) === true) {
                            reply_time = getGreyText(RegExp.$1);
                        }
                        let reply_meta = getCommentMeta('<br>' + replier + reply_time);
                        let reply_content = parseDomForHtml(commentsBlockHtmlArray[j], '.right-bottom&&Html');
                        replyComment = reply_content + reply_meta;
                    }
                    if (commentsBlockHtmlArray[j].indexOf('作者回复') > -1) {
                        try {
                            let author = getAuthorText('作者回复');
                            let time = '';
                            if (/(<span class="normal-font gray-font align-baseline">[\s\S]*?<\/span>)/.test(commentsBlockHtmlArray[j]) === true) {
                                time = getGreyText(RegExp.$1);
                            }
                            let meta = getCommentMeta('<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;' + author + time);
                            let commentContent = '<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>'
                                + parseDomForHtml(commentsBlockHtmlArray[j], '.right-bottom&&Html');
                            authorComment = commentContent + meta;
                            // authorComment = '<br>' + authorComment;
                        } catch (e) { }
                    }
                    comment = replyComment + authorComment;
                    d.push({
                        title: comment.replace(/<div class="recipe-questions-digg">/g, '')
                    });
                }
                d.push({
                    col_type: 'line'
                });
            }
        } catch (e) { }
    }
} catch (e) {  }


res.data = d;
setHomeResult(res);

